const upBtn=document.querySelector(".up-button"),downBtn=document.querySelector(".down-button"),sidebar=document.querySelector(".sidebar"),container=document.querySelector(".container"),mainSlide=document.querySelector(".main-slide"),slidesCount=mainSlide.querySelectorAll("div").length;let activeSlideIndex=0;function changeSlide(direction){"up"===direction?(activeSlideIndex++,activeSlideIndex===slidesCount&&(activeSlideIndex=0)):"down"===direction&&(activeSlideIndex--,activeSlideIndex<0&&(activeSlideIndex=slidesCount-1));const height=container.clientHeight;mainSlide.style.transform=`translateY(-${activeSlideIndex*height}px)`,sidebar.style.transform=`translateY(${activeSlideIndex*height}px)`}sidebar.style.top=`-${100*(slidesCount-1)}vh`,upBtn.addEventListener("click",()=>{changeSlide("up")}),downBtn.addEventListener("click",()=>{changeSlide("down")});